// This file is used to store / set global variables that are used across the app

// This is used to override the payload sent via the websocket
let debugWSPayload: Record<string, any> | undefined = undefined;

export const __global = {
  setDebugWSPayload: (payload: Record<string, any> | undefined) =>
    (debugWSPayload = payload),
  getDebugWSPayload: (): Record<string, any> | undefined => debugWSPayload,
  updateDebugWSPayload: (payload: Record<string, any> | undefined) => {
    debugWSPayload = {
      ...debugWSPayload,
      ...payload,
    };
  },
};
