import { Character, RecentChatShunt } from '../types/app-api';

const HiddenCharacterIds = [
  'T2j7WCwwEDJZod94YGY--FZjx0Ev-CfGTJPFfPt6jGY', // Creative Mode
];

export const filterHiddenCharacterChats = (chats: RecentChatShunt[]) => {
  if (HiddenCharacterIds.length === 0) {
    return chats;
  }
  return chats.filter(
    chat => HiddenCharacterIds.indexOf(chat.character_id) === -1,
  );
};

export const filterHiddenCharacters = (characters?: Character[]) => {
  if (HiddenCharacterIds.length === 0) {
    return characters;
  }
  return characters?.filter(
    character => HiddenCharacterIds.indexOf(character.external_id) === -1,
  );
};

